"use strict";

$(document).ready(function () {
  new Typed(".section__title_step", {
    strings: ["планам развития", "адаптации новичков", "карьерным траекториям"],
    typeSpeed: 70,
    backSpeed: 55,
    loop: !0
  });
  $('a[href^="#"').on("click", function () {
    var e = $(this).attr("href");
    if (!$(e).length) return !1;
    $("html, body").animate({
      scrollTop: $(e).offset().top - 50
    });
  }), new WOW().init(), $("#phone").mask("+7 999 999-99-99"), $(".order").validate({
    rules: {
      phone: {
        required: !0,
        minlength: 16
      },
      name: {
        required: !0
      }
    },
    errorPlacement: function errorPlacement(e, t) {},
    errorClass: "field__control_error",
    validClass: "field__control_valid",
    submitHandler: function submitHandler(e, t) {
      t.preventDefault();
      var r = $(".order").serialize();
      $.ajax({
        url: "./conf/telegram.php",
        method: "POST",
        dataType: "html",
        data: r,
        success: function success(e) {
          "ok" === e ? ($(".order__submit").text("Заявка отправлена"), $.fancybox.close(), $(".order").trigger("reset")) : alert("Произошла ошибка, проверьте корректность ввода данных.");
        }
      });
    }
  }), $(".link__item").on("click", function (e) {
    e.preventDefault(), $.fancybox.open({
      src: "#hidden",
      type: "inline"
    });
  });
});